// src/components/ParkIcons.jsx
import React from "react";
import styles from "./ParkSelect.module.css";

const ParkIcon = ({ gradientId, active, pathData, gradientColors }) => (
  <svg
    className={`${styles.parkIcon} ${active ? styles.activeSvg : ""}`}
    version="1.1"
    xmlns="http://www.w3.org/2000/svg"
    viewBox="0 0 80 80"
    xmlSpace="preserve"
  >
    <defs>
      <linearGradient id={gradientId} x1="0%" y1="0%" x2="100%" y2="100%">
        <stop
          offset="30%"
          style={{ stopColor: gradientColors[0], stopOpacity: 1 }}
        />
        <stop
          offset="100%"
          style={{ stopColor: gradientColors[1], stopOpacity: 1 }}
        />
      </linearGradient>
    </defs>
    <path
      fill={active ? `url(#${gradientId})` : "rgba(255, 255, 255, 0.7)"}
      d={pathData}
    />
  </svg>
);

